package org.apache.solr.analysis;import org.apache.solr.common.SolrInputDocument;import org.apache.lucene.analysis.util.TokenFilterFactory;/** * Abstract class for all {@link TokenFilterFactory} implementations that want * to modify {@link SolrInputDocument} during analysis. */public abstract class DocumentAlteringFilterFactory extends TokenFilterFactory {  protected SolrInputDocument solrDocument;    public void setSolrInputDocument(SolrInputDocument solrDocument) {    this.solrDocument = solrDocument;  }    public SolrInputDocument getSolrInputDocument() {    return solrDocument;  }}