package org.apache.solr.analysis;import org.apache.solr.common.SolrInputDocument;/** * Abstract class for all {@link TokenFilterFactory} implementations that want * to modify {@link SolrInputDocument} during analysis. */public abstract class DocumentAlteringFilterFactory extends BaseTokenFilterFactory {  protected SolrInputDocument solrDocument;    public void setSolrInputDocument(SolrInputDocument solrDocument) {    this.solrDocument = solrDocument;  }    public SolrInputDocument getSolrInputDocument() {    return solrDocument;  }}